_list2 = list()
#------------ PART 1 ------------
def count_trees(right, down):
    trees = 0
    ix = 0
    double_match = 0
    #for s in _list:
    for n in range(0, len(_list), down):
        if n == 0:
            _list2.append(list(_list[n]))
            continue
        
        s = _list[n]
        l = list(s)

        ix = (ix + right) % len(l)
        #print("len(l) = {} ix = {}".format(len(l),ix))

        #print("l[ix] = " + str(l[ix]))
        if l[ix] == 'X' or l[ix] == 'O':
            double_match += 1

        if l[ix] == '#':
            trees += 1
            l[ix] = 'X'
            #l[ix] = 1
        elif l[ix] == '.':
            l[ix] = 'O'
            #l[ix] = 1

        #elif isinstance(l[ix], int):
        #    print("is int")
        #    l[ix] = l[ix] + 1

        _list2.append(l)
    #print("----------------------------------")
    #for l in _list2:
    #    for c in l:
    #        print(c, end="")
    #    print("")
    print("---------------------------------")
    print("trees = " + str(trees))
    print("double match = " + str(double_match))

count_trees(1,1)
count_trees(3,1)
count_trees(5,1)
count_trees(7,1)
